import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import * as movieAPI from '../services/movieAPI';
import { Loading } from '../components';

class MovieDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: '',
      movieObj: [],
    };
    this.fetchMovieDetails = this.fetchMovieDetails.bind(this);
  }

  componentDidMount() {
    const { id } = this.props.match.params;
    this.fetchMovieDetails(id);
  }

  fetchMovieDetails(id) {
    this.setState({ loading: true },
      () => {
        movieAPI.getMovie(id)
          .then((resolve) => (
            this.setState({
              loading: false,
              movieObj: resolve,
            })
          ));
      });
  }

  // handleClick(event) {
  //   <Link to={ `/movies/${id}` }>VER DETALHES</Link>;
  // }

  renderDatails() {
    const { movieObj } = this.state;
    const { title, storyline, imagePath, genre, rating, subtitle } = movieObj;
    return (
      <div>
        <p>{ title }</p>
        <img alt="Movie Cover" src={ `../${imagePath}` } />
        <p>{ `Subtitle: ${subtitle}` }</p>
        <p>{ `Storyline: ${storyline}` }</p>
        <p>{ `Genre: ${genre}` }</p>
        <p>{ `Rating: ${rating}` }</p>
      </div>);
  };

  render() {
    const { loading } = this.state;

    if (loading) { return <Loading />; }

    return (
      <div data-testid="movie-details">
        <div>{ this.renderDatails }</div>
        <div>
          <button type="button" onClick={ handleClick() }>EDITAR</button>
          <button type="button" onClick={ handleClick() }>VOLTAR</button>
        </div>
      </div>
    );
  }
}

export default MovieDetails;
